/** * */package zphuan.com.peihuanlibrary.util;import android.util.Log;/** * @version @创建时间：2014-11-4下午12:50:01 * @author: Huan.zhong @E_mail:ZPHuan1224@qq.com * @ToDo : */public class Logger {    /**     * 控制需要打的log     * <p/>     * 5 全部显示     * <p/>     * 0不显示     */    private static int LogGrade = 5;    public static void i(String tag, String msg) {        if (LogGrade > 1) {            if (null != msg)                Log.i(tag, msg);        }    }    public static void d(String tag, String msg) {        if (LogGrade > 2) {            if (null != msg)                Log.d(tag, msg);        }    }    public static void w(String tag, String msg) {        if (LogGrade > 3) {            if (null != msg)                Log.w(tag, msg);        }    }    public static void e(String tag, String msg) {        if (LogGrade > 4) {            if (null != msg)                Log.e(tag, msg);        }    }}